mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT dbox_num, mem_num, dcate_num, dbox_team_type,
REPLACE(REPLACE(dbox_team_name,'<','&lt;'),'>','&gt;') dbox_team_name, REPLACE(REPLACE(dbox_title,'<','&lt;'),'>','&gt;') dbox_title,
dbox_team_photo, dbox_business_rnum, dbox_photo, dcate_name, dcate_icon, dbox_goal, dbox_sdate, dbox_edate, dbox_rdate, dbox_status FROM dbox JOIN dona_category USING(dcate_num)
)a) LEFT OUTER JOIN (SELECT dbox_num, SUM(dbox_do_price) total FROM dbox_donation group by dbox_num ) USING(dbox_num)
WHERE rnum >= #{start} AND rnum <= #{end}

