mapper.insert:
INSERT INTO
member_detail(
mem_num,
mem_rcode,
recommend_status
) VALUES(
#{mem_num},
#{mem_rcode},
#{recommend_status}
)

mapper.update:
UPDATE member
SET
mem_nick=#{mem_nick}
WHERE mem_num=#{mem_num}

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.*, c.mem_date, c.mem_point FROM member b LEFT OUTER JOIN member_detail c ON b.mem_num=c.mem_num
)a)
WHERE rnum >= ${start} AND rnum <= #{end}
